import * as echarts from 'echarts';
export default {
  name: "Analysis",
  data() {
    return {
      //页面中所有变量都放在这里
      tableData: [],
      // 所有的数据
      pageNum: 1,
      // 当前的页码
      pageSize: 10,
      // 每页显示的个数
      total: 0,
      title: null,
      fromVisible: false,
      //新增弹窗一开始是不可见的
      form: {},
      user: JSON.parse(localStorage.getItem('xm-user') || '{}'),
      rules: {
        title: [{
          required: true,
          message: '请输入标题',
          trigger: 'blur'
        }],
        content: [{
          required: true,
          message: '请输入内容',
          trigger: 'blur'
        }]
      },
      ids: [],
      courseData: [],
      courseId: 1 // 默认传入的courseId为1
    };
  },

  created() {
    this.load(1); //页面创建完后调用的方法，请求显示第一页的数据
    this.loadCourse();
    this.loadAverageScores();
  },
  methods: {
    loadCourse() {
      //教师只能新增自己任教的课程的成绩，所以用params插入teacherId参数作为/course/selectAll的查询条件
      //0523：这里我要改成录入成绩只由管理员完成，所以不需要传入teacherID了
      this.$request.get('/course/selectAll'
      //   params:{
      //     teacherId:this.user.role==='TEACHER'?this.user.id:null}
      // }
      ).then(res => {
        if (res.code === '200') {
          this.courseData = res.data;
        } else {
          this.$message.error(res.msg);
        }
      });
    },
    loadAverageScores() {
      this.$request.get('/score/average-by-class', {
        params: {
          courseId: this.courseId
        }
      }).then(res => {
        if (res.code === '200') {
          this.renderChart(res.data);
        } else {
          this.$message.error(res.msg);
        }
      });
    },
    renderChart(data) {
      const chart = echarts.init(document.getElementById('average-score-chart'));
      const option = {
        title: {
          text: '各班级平均分折线图'
        },
        tooltip: {
          trigger: 'axis'
        },
        xAxis: {
          type: 'category',
          data: data.map(item => item.class_name)
        },
        yAxis: {
          type: 'value'
        },
        series: [{
          name: '平均分',
          type: 'line',
          data: data.map(item => item.avg_score)
        }]
      };
      chart.setOption(option);
    },
    handleAdd() {
      // 新增数据
      this.form = {}; // 新增数据的时候清空数据
      this.fromVisible = true; // 将弹窗的fromVisible属性设为true 即可见
    },

    handleEdit(row) {
      // 编辑数据
      this.form = JSON.parse(JSON.stringify(row)); // 给form对象赋值  注意要深拷贝数据 效果是将内容显示在输入框中
      this.fromVisible = true; // 打开弹窗
    },

    save() {
      // 保存按钮触发的逻辑  它会触发新增或者更新
      this.$refs.formRef.validate(valid => {
        if (valid) {
          this.$request({
            url: this.form.id ? '/notice/update' : '/notice/add',
            //判断表单有没有id，如果有就是编辑，如果没有就是新增
            method: this.form.id ? 'PUT' : 'POST',
            data: this.form
          }).then(res => {
            if (res.code === '200') {
              // 表示成功保存
              this.$message.success('保存成功');
              this.load(1);
              this.fromVisible = false;
            } else {
              this.$message.error(res.msg); // 弹出错误的信息
            }
          });
        }
      });
    },

    del(id) {
      // 单个删除
      this.$confirm('您确定删除吗？', '确认删除', {
        type: "warning"
      }).then(response => {
        this.$request.delete('/notice/delete/' + id).then(res => {
          //把id传给后台 即通过访问notice/delete/id这个url调用通过id删除的方法
          if (res.code === '200') {
            // 表示操作成功
            this.$message.success('操作成功');
            this.load(1);
          } else {
            this.$message.error(res.msg); // 弹出错误的信息
          }
        });
      }).catch(() => {});
    },
    handleSelectionChange(rows) {
      // 当前选中的所有的行数据
      this.ids = rows.map(v => v.id); // ids是一个数组 这里是将复选中的所有id值赋值到数组中 如 [1,2]
    },

    delBatch() {
      // 批量删除
      if (!this.ids.length) {
        this.$message.warning('请选择数据');
        return;
      }
      this.$confirm('您确定批量删除这些数据吗？', '确认删除', {
        type: "warning"
      }).then(response => {
        this.$request.delete('/notice/delete/batch', {
          data: this.ids
        }).then(res => {
          if (res.code === '200') {
            // 表示操作成功
            this.$message.success('操作成功');
            this.load(1);
          } else {
            this.$message.error(res.msg); // 弹出错误的信息
          }
        });
      }).catch(() => {});
    },
    load(pageNum) {
      // 分页查询 通过输入页数来获取分页的数据
      if (pageNum) this.pageNum = pageNum;
      this.$request.get('/notice/selectPage', {
        //selectPage是NoticeService里的接口
        params: {
          pageNum: this.pageNum,
          pageSize: this.pageSize,
          title: this.title
        }
      }).then(res => {
        this.tableData = res.data?.list;
        this.total = res.data?.total;
      });
    },
    reset() {
      //重置方法（查询旁边的按钮）清空输入框，返回第一页
      this.title = null;
      this.load(1);
    },
    handleCurrentChange(pageNum) {
      this.load(pageNum);
    }
  }
};
